import React  from 'react';
import PropTypes from 'prop-types';
import IButton from './button/Button.js';
import { Collapse } from 'reactstrap';
import _ from 'lodash';

const propTypes = {
  scrollable: PropTypes.bool,
  dismissible: PropTypes.bool
};

const defaultProps = {
    dismissible: false,
    scrollable: false
};

class IBase extends React.Component {

    constructor(props) {
        super(props);
        this.state = { visible: true};
    }

    toggle = () => {this.setState({visible: !this.state.visible});}

    render(children) {
        let {
            toggle,
            dismissible,
            scrollable,
            toggleText,
            toggleButton,
            ...attributes
        } = this.props;

        if (dismissible) {
            const dismissButton = <IButton size="sm" color = {attributes.color} close onClick={() => {this.setState({visible: false});
                }} />;
            const newChildren = _.isArray(children.props.children) ? children.props.children.concat(dismissButton) : [children, dismissButton];
            children = React.cloneElement(children, {children: newChildren});
        }

        if (scrollable) {
            attributes.className = 'scroll';
        }

        if (toggle) {
            if (!toggleButton) {
                toggleButton = <IButton size="sm" color = {attributes.color} onClick={this.toggle} > {toggleText || "Toggle"} </IButton>;
            } else {
                toggleButton = React.cloneElement(toggleButton, {onClick: this.toggle});
            }
        }

        return (
            <div>
            {toggleButton}
            <Collapse
                onExited={() => { if (attributes.onDismiss) {attributes.onDismiss(attributes)}}}
                isOpen={this.state.visible} {...attributes}>
                {children}
            </Collapse>
            </div>
        );
    }
}

IBase.displayName = 'IBase';

export default IBase;
