import React , {Component} from 'react'
import DishDetail from './../presentationalComponents/DishDetailComponent';
import Menu from './MenuComponent';
import Header from './../presentationalComponents/HeaderComponent';
import Footer from './../presentationalComponents/FooterComponent';
import Home from './../presentationalComponents/HomeComponent';
import Contact from './../presentationalComponents/ContactComponent';
import About from './../presentationalComponents/AboutComponent';
import { COMMENTS } from '../shared/comments';
import { PROMOTIONS } from '../shared/promotions';
import { LEADERS } from '../shared/leaders';
import { DISHES } from '../shared/dishes';
import { Switch, Route, Redirect, withRouter } from 'react-router-dom'
import { connect } from 'react-redux';
import { addComment, fetchDishes, fetchComments, fetchPromos, fetchLeaders } from '../redux/ActionCreators';

class Main extends Component {
  constructor(props) {
    super(props);

    this.state = {
      dishes: DISHES,
      comments: COMMENTS,
      promotions: PROMOTIONS,
      leaders: LEADERS
    };
  }

  componentDidMount() {
      this.props.fetchDishes();
      this.props.fetchComments();
      this.props.fetchPromos();
      this.props.fetchLeaders();
  }

  onDishSelect(dishId) {
      this.setState({ selectedDishId: dishId});
  }

  render() {
    const HomePage = () => {
      return(
          <Home
                        dish={this.props.dishes.dishes.filter((dish) => dish.featured)[0]}
                        dishesLoading={this.props.dishes.isLoading}
                        dishErrMess={this.props.dishes.errMess}
                        promotion={this.props.promotions.promotions.filter((promo) => promo.featured)[0]}
                        promoLoading={this.props.promotions.isLoading}
                        promoErrMess={this.props.promotions.errMess}
                        leader={this.props.leaders.leaders.filter((leader) => leader.featured)[0]}
                    />
      );
    }


    const DishWithId = ({match}) => {
      return(
          <DishDetail dish={this.props.dishes.dishes.filter((dish) => dish.id === parseInt(match.params.dishId,10))[0]}
                      isLoading={this.props.dishes.isLoading}
                      errMess={this.props.dishes.errMess}
                      comments={this.props.comments.comments.filter((comment) => comment.dishId === parseInt(match.params.dishId,10))}
                      commentsErrMess={this.props.comments.errMess}
                      addComment={this.props.addComment}
                    />
      );
    };

    return (
      <div>
        <Header />
        <div>
          <Switch>
              <Route path='/home' component={HomePage} />
              <Route exact path='/aboutus' component={() => <About leaders={this.props.leaders} />} />} />
              <Route exact path='/menu' component={() => <Menu />} />
              <Route path='/menu/:dishId' component={DishWithId} />
              <Route exact path='/contactus' component={Contact} /> />
              <Redirect to="/home" />
          </Switch>
        </div>
        <Footer />
      </div>
    );
  }
}


const mapDispatchToProps = dispatch => ({
  addComment: (dishId, rating, author, comment) => dispatch(addComment(dishId, rating, author, comment)),
  fetchDishes: () => { dispatch(fetchDishes())},
  fetchComments: () => dispatch(fetchComments()),
  fetchPromos: () => dispatch(fetchPromos()),
  fetchLeaders: () => dispatch(fetchLeaders())
});


const mapStateToProps = state => {
    return {
      dishes: state.dishes,
      comments: state.comments,
      promotions: state.promotions,
      leaders: state.leaders
    }
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Main));
