import React  from 'react';
import PropTypes from 'prop-types';
import { Table } from 'reactstrap';
import IBase from '../Base.js'

const propTypes = {
    headingRow: PropTypes.arrayOf(PropTypes.string),
    rows: PropTypes.arrayOf(PropTypes.arrayOf(PropTypes.any)).isRequired,
    index: PropTypes.bool
};

const defaultProps = {
    index: true
};

class ITable extends IBase {

  render() {
    let {
      headingRow,
      rows,
      index,
      ...attributes
    } = this.props;

    const heading = headingRow.map(columnHeading => {
                                return <th>{columnHeading}</th>
                            });

    const body = rows.map((row, index) => {
        let cells = row.map(cell => {
            return (<td>{cell}</td>);
        });
        if (this.props.index) {
            const indexCell = [(<td> {index} </td>)];
            cells = indexCell.concat(cells);
        }
        return (<tr>{cells}</tr>);
    });

    const table = (
      <Table {...attributes}>
         <thead>
            <tr>
               {heading}
            </tr>
         </thead>
         <tbody>
            {body}
         </tbody>
      </Table>
    );

    return super.render(table);
  }
}

ITable.propTypes = propTypes;
ITable.defaultProps = defaultProps;
ITable.displayName = 'ITable';

export default ITable;
