import * as ActionTypes from './ActionTypes';
import { baseUrl } from '../shared/baseUrl';

const axios = require('axios');

export const addComment = (dishId, rating, author, comment) => ({
    type: ActionTypes.ADD_COMMENT,
    payload: {
        dishId: dishId,
        rating: rating,
        author: author,
        comment: comment
    }
});

export const fetchDishesAxios = () => (dispatch) => {
    axios.get(baseUrl + 'dishes')
      .then(function (response) {
        console.log(response);
        dispatch(addDishes(response.data));
      })
      .catch(function (error) {
        console.log(error);
      })
      .then(function () {
      });
};

export const fetchDishes = () => ({
    type: ActionTypes.DISHES_FETCH
});

export const dishesFailed = (errmess) => ({
    type: ActionTypes.DISHES_FAILED,
    payload: errmess
});

export const addDishes = (dishes) => ({
    type: ActionTypes.ADD_DISHES,
    payload: dishes
});

export const fetchComments = () => (dispatch) => {
    axios.get(baseUrl + 'comments')
      .then(function (response) {
        console.log(response);
        dispatch(addComments(response.data));
      })
      .catch(function (error) {
        console.log(error);
      })
      .then(function () {
      });
};

export const commentsFailed = (errmess) => ({
    type: ActionTypes.COMMENTS_FAILED,
    payload: errmess
});

export const addComments = (comments) => ({
    type: ActionTypes.ADD_COMMENTS,
    payload: comments
});

export const fetchPromos = () => (dispatch) => {
    dispatch(promosLoading());

    axios.get(baseUrl + 'promotions')
      .then(function (response) {
        console.log(response);
        dispatch(addPromos(response.data));
      })
      .catch(function (error) {
        console.log(error);
      })
      .then(function () {
      });
}

export const promosLoading = () => ({
    type: ActionTypes.PROMOS_LOADING
});

export const promosFailed = (errmess) => ({
    type: ActionTypes.PROMOS_FAILED,
    payload: errmess
});

export const addPromos = (promos) => ({
    type: ActionTypes.ADD_PROMOS,
    payload: promos
});

export const fetchLeaders = () => (dispatch) => {
    axios.get(baseUrl + 'leaders')
      .then(function (response) {
        console.log(response);
        dispatch(addLeaders(response.data));
      })
      .catch(function (error) {
        console.log(error);
      })
      .then(function () {
      });
};

export const leadersFailed = (errmess) => ({
    type: ActionTypes.LEADERS_FAILED,
    payload: errmess
});

export const addLeaders = (leaders) => ({
    type: ActionTypes.ADD_LEADERS,
    payload: leaders
});
