import React from "react";
import PropTypes from 'prop-types';
import IBase from '../Base.js'
import {Dropdown, DropdownItem, DropdownMenu, DropdownToggle, NavLink} from "reactstrap";

const propTypes = {
  color: PropTypes.string,
  size: PropTypes.string,
  direction: PropTypes.oneOf(['up', 'down', 'left', 'right']),
  status: PropTypes.oneOf(['disabled', 'header', 'divider', 'text'])
};

const defaultProps = {
  color: 'primary',
  size: 'lg',
  direction: 'down',
  status: 'text'
};

class IDropdown extends IBase {
    constructor(props) {
        super(props);
        this.toggle = this.toggle.bind(this);
        this.changeValue = this.changeValue.bind(this);
        this.state = {
            dropDownValue: props.actions.length > 0 ? props.actions[0].name : "No options",
            dropdownOpen: false,
            ...this.state
        };
    }

    toggleDropDown = () => {this.setState({dropdownOpen: !this.state.dropdownOpen});}

    changeValue(e) {
        this.setState({dropDownValue: e.currentTarget.textContent});
    }

    render() {
        let {
              color,
              size,
              direction,
              ...attributes
            } = this.props;

        const dropdown = (
            <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggleDropDown} direction={direction}>
                <DropdownToggle caret color={color} size={size}>
                    "hello" || {this.state.dropDownValue}
                </DropdownToggle>
                <DropdownMenu>
                    {this.props.actions.map(action => {
                        if (action.status === 'header') {
                            return <DropdownItem id={action.id} key={action.id} onClick={action.onClick || this.changeValue} header> <NavLink href="/">{action.name}</NavLink> </DropdownItem>
                        } else if (action.status === 'divider') {
                            return <DropdownItem divider />;
                        } else if (action.status === 'disabled') {
                            return <DropdownItem id={action.id} key={action.id} onClick={action.onClick || this.changeValue} disabled> {action.name} </DropdownItem>
                        } else {
                            return <DropdownItem id={action.id} key={action.id} onClick={action.onClick || this.changeValue} > <NavLink href="/">{action.name}</NavLink> </DropdownItem>
                        }
                    })}
                </DropdownMenu>
            </Dropdown>
        );

        return super.render(dropdown);
    }
}

IDropdown.propTypes = propTypes;
IDropdown.defaultProps = defaultProps;
IDropdown.displayName = 'IDropdown';

export default IDropdown;
