import React  from 'react';
import PropTypes from 'prop-types';
import { Alert as RSAlert } from 'reactstrap';
import IBase from '../Base.js'
import { Collapse } from 'reactstrap';
import IButton from '../button/Button.js';
import _ from 'lodash';

const propTypes = {
  color: PropTypes.string
};

const defaultProps = {
  color: 'primary',
};

const withVisibilityControl = (WrappedComponent) => {
  return class ControlVisibility extends React.Component {
    constructor(props) {
      super(props);

      this.state = {
        visible: true
      };
    }

//    toggle = () => {this.setState({visible: !this.state.visible});}

    render() {

      let {
          dismissible,
          ...passThroughProps
      } = this.props;

      if (dismissible) {
          const dismissButton = <IButton size="sm" color = {passThroughProps.color} close onClick={() => {this.setState({visible: false});
              }} />;
//          const newChildren = _.isArray(WrappedComponent.props.children) ? children.props.children.concat(dismissButton) : [children, dismissButton];
//          children = React.cloneElement(children, {children: newChildren});
      }

      const injectedProp = {
        dismissible: true
      };

      return (
          <div>
          <Collapse
              onExited={() => { if (passThroughProps.onDismiss) {passThroughProps.onDismiss(passThroughProps)}}}
              isOpen={this.state.visible} {...passThroughProps}>
              <WrappedComponent
                    injectedProp={injectedProp}
                    {...passThroughProps}
              />
          </Collapse>
          </div>
      );
    }
  }
}

class IAlert extends React.Component {

  render() {
    let {
      color,
      ...attributes
    } = this.props;

    if (attributes.children === undefined) {
       attributes.children = <p>This is an alert. </p>
    }

    const alert = (
      <RSAlert
        color = {color}
        {...attributes}
      >
        {attributes.children}
      </RSAlert>
    );

    return super.render(alert);
  }
}

IAlert.propTypes = propTypes;
IAlert.defaultProps = defaultProps;
IAlert.displayName = 'IAlert';

export default withVisibilityControl(IAlert);
